                if ((double)this.velocity.Y < 0.0)
                    this.velocity.Y *= 0.9f;
                this.velocity.Y += 0.1f;
                if ((double)this.velocity.Y > 3.0)
                    this.velocity.Y = 3f;
            }
            else if ((double)this.velocity.Y < -0.1 || (double)this.velocity.Y > 0.1)
