                        num = 93;
                        break;
                    case 61:
                        num = 94;
                        break;
                    case 62:
                        num = 95;
                        break;
                    case 63:
                        num = 96;
                        break;
                    case 15:
                        num = 88;
                        break;
                    case 36:
                        num = 89;
                        break;
                }
                if (num != -1)
                    somethingSpecial = true;
            }
            if (armorslot == 2)
