        public bool CheckMana(int amount, bool pay = false, bool blockQuickMana = false)
        {
            int num = (int)((double)amount * (double)this.manaCost);
            if (this.statMana >= num)
            {
                if (pay)
                    this.statMana -= num;
                return true;
            }
            if (!this.manaFlower || blockQuickMana)
                return false;
            this.QuickMana();
            if (this.statMana < num)
                return false;
            if (pay)
                this.statMana -= num;
            return true;
        }

