            int num4 = Netplay.GetSectionY(endY);
            for (int i = sectionX; i <= num3; i++)
            {
                for (int j = sectionY; j <= num4; j++)
                {
                    if (Main.sectionManager.SectionLoaded(i, j) && !Main.sectionManager.SectionFramed(i, j))
